/*Table structure for table `st_vote` */

DROP TABLE IF EXISTS `st_vote`;

CREATE TABLE `st_vote` (
  `id` int(11) unsigned NOT NULL,
  `user_id` int(11) unsigned NOT NULL,
  `name` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,
  `ip_address` varchar(15) COLLATE utf8_unicode_ci NOT NULL,
  `vote` tinyint(5) unsigned NOT NULL COMMENT 'điểm vote',
  `message` tinytext COLLATE utf8_unicode_ci DEFAULT NULL,
  `is_status` tinyint(2) unsigned NOT NULL DEFAULT 2,
  `created_time` datetime NOT NULL DEFAULT current_timestamp(),
  `updated_time` datetime NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),
  UNIQUE KEY `unique_vote` (`id`,`ip_address`),
  KEY `fk_vote_uid` (`user_id`),
  CONSTRAINT `fk_vote_uid` FOREIGN KEY (`user_id`) REFERENCES `st_users` (`id`) ON DELETE CASCADE ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

